execution.script:
pipeline {
        agent any
		environment {
		ansible_userid = credentials('ansible_userid')
        ansible_password = credentials('ansible_password')
        VCentre_Username = credentials('VCentre_Username')
        VCentre_Password = credentials('VCentre_Password')

        }
        stages {
                stage('Validating Parameters') {
                        steps {

                                dir("/var/lib/jenkins/workspace/enhance_harddisk") {
                                        sh '''
if [ "$VCenter_Hostname" == "xda" ];then
VCentre_Hostname="10.8.87.43"
elif [ "$VCenter_Hostname" == "xdb" ];then
VCentre_Hostname="10.8.87.39"
elif [ "$VCenter_Hostname" == "xdc" ];then
VCentre_Hostname="10.8.87.40"
fi
if [ "$Enhance_Disk" == "1 GB" ];then
Enhance_Disks="1"
elif [ "$Enhance_Disk" == "2 GB" ];then
Enhance_Disks="2"
elif [ "$Enhance_Disk" == "3 GB" ];then
Enhance_Disks="3"
fi
cat <<EOT > /var/lib/jenkins/workspace/enhance_harddisk/hosts
vcenter_hostname: "$VCentre_Hostname"
vcenter_username: "$VCentre_Username"
hostname: "$Server"
enhance_disk: "$Enhance_Disks"
device_id: "$Drive_Letter"
EOT
'''
                                        }

                                }

                        }
                stage('Validating Hostname'){
							steps {
								dir("/var/lib/jenkins/workspace/enhance_harddisk") {
								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags precheck --extra-vars @/var/lib/jenkins/workspace/enhance_harddisk/hosts'
									}
								}
					   }        
               stage('Performing Pre-Checks') {

					parallel {
					    stage('Validating Server Reachability'){
							steps {
								dir("/var/lib/jenkins/workspace/enhance_harddisk") {
								    sh '#sh inventory.sh'
									sh '#ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags medcheck_startup --extra-vars @/var/lib/jenkins/workspace/enhance_harddisk/hosts'
									}
								}
					   }
						stage('Validating vCenter connectivity'){
							steps {
								dir("/var/lib/jenkins/workspace/enhance_harddisk") {
								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags precheck_vconnection --extra-vars @/var/lib/jenkins/workspace/enhance_harddisk/hosts'
									}
								}
					   }
					   stage('Validating datastore size'){
							steps {
								dir("/var/lib/jenkins/workspace/enhance_harddisk") {
								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags "precheck_harddisk,precheck_datastore" --extra-vars @/var/lib/jenkins/workspace/enhance_harddisk/hosts'
									}
								}
					   }
					   stage('Validating Snapshot Existence'){
							steps {
								dir("/var/lib/jenkins/workspace/enhance_harddisk") {
								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags postcheck_snapshotcreated --extra-vars @/var/lib/jenkins/workspace/enhance_harddisk/hosts'
									}
								}
					   }


                    }
                } 
               stage('Enhance Harddisk for hosted VM'){
					steps {
						dir("/var/lib/jenkins/workspace/enhance_harddisk") {

                                    sh 'sh inventory.sh'
									sh 'ansible-playbook main.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags "harddisk" -vvv --extra-vars @/var/lib/jenkins/workspace/enhance_harddisk/hosts'


                            }

						}
                    }		

				stage('Performing post-Checks') {

					parallel {
					    stage('Validating Datastore Free Space'){
							steps {
								dir("/var/lib/jenkins/workspace/enhance_harddisk") {
								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags precheck_datastore --extra-vars @/var/lib/jenkins/workspace/enhance_harddisk/hosts'
									}
								}
					   }
					   stage('Validating Server Reachability'){
							steps {
								dir("/var/lib/jenkins/workspace/enhance_harddisk") {
								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags medcheck_startup --extra-vars @/var/lib/jenkins/workspace/enhance_harddisk/hosts'
									}
								}
					   }
					stage('Allocating space'){
					steps {
						dir("/var/lib/jenkins/workspace/enhance_harddisk") {

                                    sh 'sh inventory.sh'
									sh 'ansible-playbook main.yml -i inventory_host -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags "harddisk_new" -vvv --extra-vars @/var/lib/jenkins/workspace/enhance_harddisk/hosts'


                            }

						}
                    }

					}
				}
				stage('Completed'){
							steps {								
								dir("/var/lib/jenkins/workspace/enhance_harddisk") {								    

										}
									} 
				        }
			}			






	post {
        always {
            sh '#echo "" > /var/lib/jenkins/workspace/enhance_harddisk/hosts'
			sh '#echo "" > /var/lib/jenkins/workspace/enhance_harddisk/inventory'
			sh '#echo "" > /var/lib/jenkins/workspace/enhance_harddisk/inventory_host'
        }

	}
}
