definition.script:
pipeline {
        agent any
        stages {
                stage('Code Quality Check') {
                        steps {
                                dir("/var/lib/jenkins/workspace/${JOB_NAME}/Services_Start/") {
                                        sh "echo $Host > inventory"
                                        sh "sh breakhost.sh"
                                        sh "echo $Service_name  > serv.txt"
                                        sh "sh linebreak.sh"
                                        sh "sh server.sh"

                                }
                        }
                }
                stage('Port Availability Check') {
                        steps {
                                dir("/var/lib/jenkins/workspace/${JOB_NAME}/Services_Start/") {
                                        sh 'sudo ansible-playbook Service_start.yml -i inventory --tags=port_check'
                                        }
                                }
                        }
                stage('Disk Health Check') {
                        steps {
                                dir("/var/lib/jenkins/workspace/${JOB_NAME}/Services_Start/") {
                                        sh 'sudo ansible-playbook Service_start.yml -i inventory --tags=disk_check'
                                        }
                                }
                        }
                stage('Restarting System Service') {
                        steps {
                                dir("/var/lib/jenkins/workspace/${JOB_NAME}/Services_Start/") {
                                       sh "sudo ansible-playbook Service_start.yml --tags=main"
                                }
                        }
                }
                stage('Service Status Check') {
                        steps {
                                dir("/var/lib/jenkins/workspace/${JOB_NAME}/Services_Start/") {
                                        sh "sudo ansible-playbook Service_start.yml --skip-tags='IntegerationTest,main'"
                                }
                        }
                }
        }
        post {
                success {
                        echo "Services started successfully"
                }
                failure {
                        echo "Service startup failed"
                }
        }
}
