definition.script:
pipeline {
        agent any
		environment {
		ansible_userid = credentials('ansible_userid')
                ansible_password = credentials('ansible_password')
                VCentre_Username = credentials('VCentre_Username')
        VCentre_Password = credentials('VCentre_Password')

        }
        stages {
                stage('Validating Parameter') {

                         steps {

                              dir("/var/lib/jenkins/workspace/orphan_vmdkreport") {
                                                                      sh '''
cat <<EOT > /var/lib/jenkins/workspace/orphan_vmdkreport/roles/orphan_vmdk/files/server_ip.txt
$VCenter_Hostname
EOT
value="$(cat /var/lib/jenkins/workspace/orphan_vmdkreport/roles/orphan_vmdk/files/server_ip.txt | tr -s ' ' | cut -d ' ' -f 2 | tr '\n' ',' | sed 's/,$//')"
echo "$value" > ips.txt
echo $VCentre_Username
echo $VCentre_Password
cat <<EOT > /var/lib/jenkins/workspace/orphan_vmdkreport/roles/orphan_vmdk/files/hosts.txt
$VCentre_Username
$VCentre_Password
EOT
'''

                                }
                        }
                }
                stage('Validating Hostname'){
							steps {
dir("/var/lib/jenkins/workspace/orphan_vmdkreport") {
								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_username=$VCentre_Username vcenter_password=$VCentre_Password" --tags precheck'
									}
								}
					   }
                stage('Orphan vmdk report generation'){
					steps {
						dir("/var/lib/jenkins/workspace/orphan_vmdkreport") {

                                    sh 'sh inventory.sh'
				    sh 'ansible-playbook site.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password"'


                            }

						}
                    }	
                stage('Completed'){
							steps {
dir("/var/lib/jenkins/workspace/orphan_vmdkreport") {

									}
								}
					   }	
		}
}
