definition.script:
pipeline {
        agent any
		environment {
		ansible_userid = credentials('ansible_userid')
        ansible_password = credentials('ansible_password')
        VCentre_Username = credentials('VCentre_Username')
        VCentre_Password = credentials('VCentre_Password')

        }
        stages {
                stage('Validating Parameters') {
                        steps {

                                dir("/var/lib/jenkins/workspace/prod/snapshot_creation") {
                                        sh '''
if [ "$VCenter_Hostname" == "DC8" ];then
VCentre_Hostname="10.8.87.39,10.8.87.43"
elif [ "$VCenter_Hostname" == "DC9" ];then
VCentre_Hostname="10.8.87.40,10.8.87.41,10.8.87.39"
elif [ "$VCenter_Hostname" == "DC10" ];then
VCentre_Hostname="10.8.87.40,10.8.87.41,10.8.87.39"
fi
echo "$VCentre_Hostname" > VC.txt
cat <<EOT > /var/lib/jenkins/workspace/prod/snapshot_creation/server_ip.txt
$Inventory_Hostname
EOT
value="$(cat /var/lib/jenkins/workspace/prod/snapshot_creation/server_ip.txt | tr -s ' ' | cut -d ' ' -f 2 | tr '\n' ',' | sed 's/,$//')"
echo "$value" > Host_Snap.txt
cat <<EOT > /var/lib/jenkins/workspace/prod/snapshot_creation/hosts
vcenter_username: "$VCentre_Username"
snapshot_name: "$Snapshot_Name"
description: "$Description"
EOT
'''
                                        }

                                }

                        }

                stage('Validating Hostname'){
							steps {
								dir("/var/lib/jenkins/workspace/prod/snapshot_creation") {
								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags validate_connection --extra-vars @/var/lib/jenkins/workspace/prod/snapshot_creation/hosts'
									}
								}
					   }
                stage('Performing Pre-Checks') {

					parallel {
						stage('Validating vCenter connectivity'){
							steps {
								dir("/var/lib/jenkins/workspace/prod/snapshot_creation") {
								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags "precheck_connection" --extra-vars @/var/lib/jenkins/workspace/prod/snapshot_creation/hosts'
									}
								}
					   }
					   stage('Validating datastore size and vm size'){
							steps {
								dir("/var/lib/jenkins/workspace/prod/snapshot_creation") {
								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags "precheck_harddisk,precheck_datastore" --extra-vars @/var/lib/jenkins/workspace/prod/snapshot_creation/hosts'
									}
								}
					   }

					   stage('Validating Server Reachability'){
							steps {
								dir("/var/lib/jenkins/workspace/prod/snapshot_creation") {
								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags "medcheck_startup" --extra-vars @/var/lib/jenkins/workspace/prod/snapshot_creation/hosts'
									}
								}
					   }


                    }
                }
                         stage('Create Snapshot for hosted VM'){
					steps {
						dir("/var/lib/jenkins/workspace/prod/snapshot_creation") {

                                    sh 'sh inventory.sh'
									sh 'ansible-playbook main.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags "snapshot_creation,vm_folder" --extra-vars @/var/lib/jenkins/workspace/prod/snapshot_creation/hosts'


                            }

						}
                    }		

				stage('Performing post-Checks') {

					parallel {
					    stage('Validating Snapshot Existence'){
							steps {
								dir("/var/lib/jenkins/workspace/prod/snapshot_creation") {
								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags "postcheck_snapshotcreated" --extra-vars @/var/lib/jenkins/workspace/prod/snapshot_creation/hosts'
									}
								}
					   }
						stage('Validating Datastore Free Space'){
							steps {
								dir("/var/lib/jenkins/workspace/prod/snapshot_creation") {
								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags "precheck_datastore" --extra-vars @/var/lib/jenkins/workspace/prod/snapshot_creation/hosts'
									}
								}
					   }
					   stage('Validating Server Reachability'){
							steps {
								dir("/var/lib/jenkins/workspace/prod/snapshot_creation") {
								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags "medcheck_startup" --extra-vars @/var/lib/jenkins/workspace/prod/snapshot_creation/hosts'
									}
								}
					   }
					}
				}
				stage('Completed'){
							steps {								
								dir("/var/lib/jenkins/workspace/prod/snapshot_creation") {								    

										}
									} 
				        }
			}			






	post {
        always {
            sh '#echo "" > /var/lib/jenkins/workspace/prod/snapshot_creation/hosts'
	    sh '#echo "" > /var/lib/jenkins/workspace/prod/snapshot_creation/inventory'
        }

	}
}
