definition.script:
pipeline {
        agent any
		environment {
		ansible_userid = credentials('ansible_userid')
        ansible_password = credentials('ansible_password')
        VCentre_Username = credentials('VCentre_Username')
        VCentre_Password = credentials('VCentre_Password')

        }
        stages {
                stage('Validating Parameters') {
                        steps {

                                dir("/var/lib/jenkins/workspace/prod/snapshot_deletion") {
                                        sh '''
if [ $Remove == "Remove All" ];then
$Specify_Snapshot_Name == "" 
Specify_Snapshot_Name == "$Specify_Snapshot_Name"
elif [ $Remove == "Specify Snapshot Name" ];then
Specify_Snapshot_Name == "$Specify_Snapshot_Name"
elif [ $Remove == "14 Days" ];then
Specify_Snapshot_Name == "14"
fi
if [ "$VCenter_Hostname" == "DC8" ];then
VCentre_Hostname="10.8.87.39,10.8.87.43"
elif [ "$VCenter_Hostname" == "DC9" ];then
VCentre_Hostname="10.8.87.40,10.8.87.41,10.8.87.39"
fi
echo "$VCentre_Hostname" > VC.txt
cat <<EOT > /var/lib/jenkins/workspace/prod/snapshot_deletion/server_ip.txt
$Inventory_Hostname
EOT
value="$(cat /var/lib/jenkins/workspace/prod/snapshot_deletion/server_ip.txt | tr -s ' ' | cut -d ' ' -f 2 | tr '\n' ',' | sed 's/,$//')"
echo "$value" > Host_Snap.txt
cat <<EOT > /var/lib/jenkins/workspace/snapshot_deletion/hosts
vcenter_username: "$VCentre_Username"
snapshot_name: "$Specify_Snapshot_Name"
EOT
'''
                                        }

                                }

                        }

                 stage('Validating Hostname'){
							steps {
								dir("/var/lib/jenkins/workspace/prod/snapshot_deletion") {
								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags validate_connection --extra-vars @/var/lib/jenkins/workspace/prod/snapshot_deletion/hosts'
									}
								}
					   }
                stage('Performing Pre-Checks') {

					parallel {
						stage('Validating vCenter connectivity'){
							steps {
								dir("/var/lib/jenkins/workspace/prod/snapshot_deletion") {
								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags precheck_vconnection --extra-vars @/var/lib/jenkins/workspace/prod/snapshot_deletion/hosts'
									}
								}
					   }
					   stage('Validating Snapshot Existence'){
							steps {
								dir("/var/lib/jenkins/workspace/prod/snapshot_deletion") {
								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags precheck_snapshotcreated --extra-vars @/var/lib/jenkins/workspace/prod/snapshot_deletion/hosts'
									}
								}
					   }
					   stage('Validating Server Reachability'){
							steps {
								dir("/var/lib/jenkins/workspace/prod/snapshot_deletion") {
								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags medcheck_startup --extra-vars @/var/lib/jenkins/workspace/prod/snapshot_deletion/hosts'
									}
								}
					   }


                    }
                }
                         stage('Remove Snapshot for hosted VM'){
					steps {
						dir("/var/lib/jenkins/workspace/prod/snapshot_deletion") {
						    script {
						        if (params.Remove == 'Remove All') {
						            sh 'sh inventory.sh'
									sh 'ansible-playbook main.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags "snapshot_deletion_removeall,vm_folder" --extra-vars @/var/lib/jenkins/workspace/prod/snapshot_deletion/hosts'

						        }
						        else if (params.Remove == '14 Days') {
						            sh 'sh inventory.sh'
									sh 'ansible-playbook main.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags "snapshot_delete_specificdate,vm_folder" --extra-vars @/var/lib/jenkins/workspace/prod/snapshot_deletion/hosts'

						        }
						        else if (params.Remove == 'Specify Snapshot Name'){
                                    sh 'sh inventory.sh'
									sh 'ansible-playbook main.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags "snapshot_deletion,vm_folder" --extra-vars @/var/lib/jenkins/workspace/prod/snapshot_deletion/hosts'
						        }
						    }
                            }

						}
                    }		

				stage('Performing post-Checks') {

					parallel {

						stage('Validating Datastore Free Space'){
							steps {
								dir("/var/lib/jenkins/workspace/prod/snapshot_deletion") {
								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags postcheck_datastore --extra-vars @/var/lib/jenkins/workspace/prod/snapshot_deletion/hosts'
									}
								}
					   }
					   stage('Validating Server Reachability'){
							steps {
								dir("/var/lib/jenkins/workspace/prod/snapshot_deletion") {
								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags medcheck_startup --extra-vars @/var/lib/jenkins/workspace/prod/snapshot_deletion/hosts'
									}
								}
					   }
					}
				}
				stage('Completed'){
							steps {								
								dir("/var/lib/jenkins/workspace/prod/snapshot_deletion") {								    

										}
									} 
				        }
			}			






	post {
        always {
            sh '#echo "" > /var/lib/jenkins/workspace/prod/snapshot_deletion/hosts'
			sh '#echo "" > /var/lib/jenkins/workspace/prod/snapshot_deletion/inventory'
        }

	}
}
