definition.script:
pipeline {
        agent any
        environment {
              FOO = "$env.BUILD_NUMBER"
           }
         stages {
               stage('Code Quality')
                {
                        steps {
                                dir("/var/lib/jenkins/workspace/prod/Linux-Disk-Report/Linux-DiskClean-report/")
                                {
                                        sh 'sh server.sh'
                                        sh 'echo jobno: "$FOO" >> DiskReport/vars/main.yml'
                                 }
                               }
                 }
                 stage('Generate Report')
                {
                        steps {
                                dir("/var/lib/jenkins/workspace/prod/Linux-Disk-Report/Linux-DiskClean-report/")
                                {
                                        sh 'sudo ansible-playbook main.yml -i inventory --tags=report'
                                 }
                               }
                 }
                 stage('clearing code')
                {
                        steps {
                                dir("/var/lib/jenkins/workspace/prod/Linux-Disk-Report/Linux-DiskClean-report/")
                                {
                                        sh 'sudo ansible-playbook main.yml -i inventory --tags=clear'
                                 }
                               }
                 }

}
}
