definition.script:
pipeline {
        agent any
        environment {
		ansible_userid = credentials('rundec_user')
        ansible_password = credentials('rundeck_pass')

        }
         stages {
               stage('Validating')
                {
                        steps {
                                dir("/var/lib/jenkins/workspace/Rundeck-cleanup/")
                                {

                                sh 'sh server.sh'
                                }
                               }
                 }
                 stage('Removing Nodes')
                {
                        steps {
                                dir("/var/lib/jenkins/workspace/Rundeck-cleanup/")
                                {

                                sh 'ansible-playbook main.yml -i inventory.txt --tags=rundeck -e "ansible_user=$ansible_userid ansible_password=$ansible_password"'   
                                }
                               }
                 }
				 stage('completed')
                {
                        steps {
                                dir("/var/lib/jenkins/workspace/Rundeck-cleanup/")
                                {


                                }
                               }
                 }

}
}
