definition.script:
pipeline {
        agent any
		environment {
		ansible_userid = credentials('ansible_userid')
                ansible_password = credentials('ansible_password')
                VCentre_Username = credentials('VCentre_Username')
        VCentre_Password = credentials('VCentre_Password')

        }
        stages {
                stage('Validating Parameter') {

                         steps {

                              dir("/var/lib/jenkins/workspace/vm_detailspecific") {
                                                                      sh '''
cat <<EOT > /var/lib/jenkins/workspace/vm_detailspecific/roles/vm_detail/files/server_ip.txt
$VCenter_Hostname
EOT
cat <<EOT > /var/lib/jenkins/workspace/vm_detailspecific/roles/vm_detail/files/hosts.txt
$VCentre_Username
$VCentre_Password
EOT
cat <<EOT > /var/lib/jenkins/workspace/vm_detailspecific/roles/vm_detail/files/vm_name.txt
$VM_Name
EOT
vm_value="$(cat /var/lib/jenkins/workspace/vm_detailspecific/roles/vm_detail/files/vm_name.txt | tr -s ' ' | cut -d ' ' -f 2 | tr '\n' ',' | sed 's/,$//')"
echo "$vm_value" > vm_specific.txt
'''

                                }
                        }
                }
                stage('Validating Hostname'){
							steps {
dir("/var/lib/jenkins/workspace/vm_detailspecific") {
								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_username=$VCentre_Username vcenter_password=$VCentre_Password" --tags precheck'
									}
								}
					   }
                stage('VM Detail report generation'){
					steps {
						dir("/var/lib/jenkins/workspace/vm_detailspecific") {

                                    sh 'sh inventory.sh'
				    sh 'ansible-playbook site.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password"'


                            }

						}
                    }	
                stage('Completed'){
							steps {
dir("/var/lib/jenkins/workspace/vm_detailspecific") {

									}
								}
					   }	
		}
           }
