definition.script:
pipeline {
        agent any
		environment {
        ansible_userid = credentials('ansible_userid')
        ansible_password = credentials('ansible_password')
        VCentre_Username = credentials('VCentre_Username')
        VCentre_Password = credentials('VCentre_Password')

        }
        stages {
                stage('Validating Parameters') {
                        steps {

                                dir("/var/lib/jenkins/workspace/vm_onoff") {
                                        sh '''
if [ "$VCenter_Hostname" == "xda" ];then
VCentre_Hostname="10.8.87.43"
elif [ "$VCenter_Hostname" == "xdb" ];then
VCentre_Hostname="10.8.87.39"
elif [ "$VCenter_Hostname" == "xdc" ];then
VCentre_Hostname="10.8.87.39"
fi
cat <<EOT > /var/lib/jenkins/workspace/vm_onoff/hosts
vcenter_hostname: "$VCentre_Hostname"
vcenter_username: "$VCentre_Username"
hostname: "$Server"
state: "$State"
EOT
'''
                                        }

                                }

                        }

                stage('Validating Hostname'){
							steps {
dir("/var/lib/jenkins/workspace/vm_onoff") {

								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags precheck --extra-vars @/var/lib/jenkins/workspace/vm_onoff/hosts'
									}
								}
					   }
                stage('Performing Pre-Checks') {

					parallel {
						stage('Validating vCenter connectivity'){
							steps {
								dir("/var/lib/jenkins/workspace/vm_onoff") {

								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags precheck_connection --extra-vars @/var/lib/jenkins/workspace/vm_onoff/hosts'
									}
								}
					   }


					   stage('Validating Server Reachability'){
							steps {
								dir("/var/lib/jenkins/workspace/vm_onoff") {

								    sh 'sh inventory.sh'
									sh 'ansible-playbook check.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags medcheck_startup --extra-vars @/var/lib/jenkins/workspace/vm_onoff/hosts'
									}
								}
					   }


                    }
                }
                         stage('Power on and off of VMs'){
					steps {
						dir("/var/lib/jenkins/workspace/vm_onoff") {

                            script {	
                            if ( params.State == 'powered-on') {

                                    sh 'sh inventory.sh'
				    sh 'ansible-playbook main.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags "vm_folder,powerON_vm"  --extra-vars @/var/lib/jenkins/workspace/vm_onoff/hosts'
									}
                            else if ( params.State == 'powered-off') {
                                    sh 'sh inventory.sh'
				    sh 'ansible-playbook main.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --tags "vm_folder,powerOff_vm"  --extra-vars @/var/lib/jenkins/workspace/vm_onoff/hosts'
									}
                                }
						}	

                                }

						}




				stage('Completed'){
							steps {								
								dir("/var/lib/jenkins/workspace/vm_onoff") {								    

										}
									} 
				        }
			}			






	post {
        always {
            sh '#echo "" > /var/lib/jenkins/workspace/vm_onoff/hosts'
			sh '#echo "" > /var/lib/jenkins/workspace/vm_onoff"/inventory'
        }

	}
}
