definition.script:
pipeline {
        agent any
        environment {
		       ansible_userid = credentials('ansible_userid')
                ansible_password = credentials('ansible_password')

        }

         stages {
               stage('Validating')
                {
                        steps {
                                dir("/var/lib/jenkins/workspace/WSUSPatch-Release-Report")
                                {
                                  sh 'sh server_add.sh'
                                 }
                               }
                 }
                 stage('Generating Report')
                {
                        steps {
                                dir("/var/lib/jenkins/workspace/WSUSPatch-Release-Report")
                                {
                                   sh 'ansible-playbook WSUS_Patch.yml -i inventory.txt -e "ansible_user=$ansible_userid ansible_password=$ansible_password"'
                                 }
                               }
                 }
				 stage('Completed')
                {
                        steps {
                                dir("/var/lib/jenkins/workspace/WSUSPatch-Release-Report")
                                {

                                 }
                               }
                 }
}
}
