definition.script:
pipeline {
        agent any
		environment {
		ansible_userid = credentials('ansible_userid')
                ansible_password = credentials('ansible_password')
                VCentre_Username = credentials('VCentre_Username')
        VCentre_Password = credentials('VCentre_Password')

        }
        stages {
                stage('Validating Parameter') {

                         steps {

                              dir("/var/lib/jenkins/workspace/wwpn_report") {
                                                                      sh '''
cat <<EOT > /var/lib/jenkins/workspace/wwpn_report/hosts
vcenter_hostname: "$VCenter_Hostname"
vcenter_username: "$VCentre_Username"
EOT
'''

                                }
                        }
                }

                stage('VM WWPN report generation'){
					steps {
						dir("/var/lib/jenkins/workspace/wwpn_report") {

                                    sh 'sh inventory.sh'
				    sh 'ansible-playbook site.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password vcenter_password=$VCentre_Password" --extra-vars @/var/lib/jenkins/workspace/wwpn_report/hosts'


                            }

						}
                    }	
                stage('Completed'){
							steps {
dir("/var/lib/jenkins/workspace/wwpn_report") {

									}
								}
					   }	
		}
           }
