definition.script:
pipeline {
        agent any
		environment {
	    ansible_userid = credentials('ansible_userid')
        ansible_password = credentials('ansible_password')
        }

        stages {
		stage('Validating Parameters'){
	steps {
		dir("/var/lib/jenkins/workspace/wintel_reboot")
		{
		  sh 'echo $HostNameIP'
          sh 'sh inventory.sh'		  
									}
								}
                       }
stage('reboot server')
{
	steps {
		dir("/var/lib/jenkins/workspace/wintel_reboot")
		{
		     sh 'ansible-playbook main.yml -i inventory -e "ansible_user=$ansible_userid ansible_password=$ansible_password"'       
									}
								}
                       }
   stage('Completed')
   {
	steps {
		dir("/var/lib/jenkins/workspace/wintel_reboot") 
		{

									}
								}
                       }

        }
}
